SELECT MIN("HOUSEHOLDS"."YEAR" + "EXPENDITURES"."HOUSEHOLD_ID") as agg_0, AVG("HOUSEHOLDS"."INCOME_RANK_3") as agg_1 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_5" <= 0.536387589557108