SELECT SUM("HOUSEHOLD_MEMBERS"."WORK_STATUS" + "HOUSEHOLD_MEMBERS"."SEX") as agg_0, AVG("HOUSEHOLD_MEMBERS"."WORK_STATUS") as agg_1, MAX("HOUSEHOLD_MEMBERS"."MARITAL") as agg_2 FROM "HOUSEHOLD_MEMBERS"  WHERE "HOUSEHOLD_MEMBERS"."MARITAL" >= 5 AND "HOUSEHOLD_MEMBERS"."AGE" != 9 AND "HOUSEHOLD_MEMBERS"."YEAR" >= 2015 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" BETWEEN 1.0 AND 1.0181742555331796