SELECT SUM("provider"."region_no") as agg_0, COUNT(*) as agg_1 FROM "catery" LEFT OUTER JOIN "charge" ON "catery"."catery_no" = "charge"."catery_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no"  WHERE ("catery"."catery_desc" IN ('Travel', 'Home Supplies', 'Groceries', 'Entertainment', 'Misc', 'Electronics', 'Communication', 'Lodging') OR "catery"."catery_no" BETWEEN 2 AND 29) AND "provider"."provider_name" LIKE '%Just%' AND "catery"."catery_desc" LIKE '%Misc%'