SELECT SUM("payment"."statement_no") as agg_0, COUNT(*) as agg_1 FROM "member" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no"  WHERE "region"."country" = 'Ca' AND "statement"."statement_dt" LIKE '%1999-08-13%' AND ("statement"."due_dt" = '1999-10-03 00:00:00' OR ("statement"."member_no" BETWEEN 1853 AND 8362 AND "statement"."due_dt" LIKE '%00:00:00%'))