SELECT AVG("payment"."payment_amt" + "provider"."mail_code") as agg_0, MAX("member"."member_code" + "payment"."statement_no") as agg_1 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no"  WHERE "statement"."due_dt" LIKE '%199%9-10-03%' AND "member"."city" != ' ' AND "region"."region_name" LIKE '%Chi%na%'