SELECT COUNT(*) as agg_0, AVG("charge"."member_no") as agg_1, MAX("charge"."provider_no") as agg_2 FROM "payment" LEFT OUTER JOIN "member" ON "payment"."member_no" = "member"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no"  WHERE "payment"."payment_dt" = '1999-10-03 00:00:00' AND "charge"."provider_no" >= 225 AND "region"."street" IN ('333 Third St.', '999 Ninth St.', '222 Second St.', '111 First St.') AND "region"."country" = 'Br' AND "region"."city" NOT LIKE '%Budapest%'