SELECT MAX("member"."curr_balance" + "payment"."statement_no") as agg_0 FROM "payment" LEFT OUTER JOIN "member" ON "payment"."member_no" = "member"."member_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE "statement"."due_dt" != '1999-09-02 00:00:00' AND "payment"."payment_dt" LIKE '%00:00%:00%'