SELECT SUM("member"."member_no" + "region"."mail_code") as agg_0 FROM "region" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no"  WHERE "payment"."payment_dt" NOT LIKE '%00:00:0%0%' AND "statement"."statement_dt" IN ('1999-08-13 00:00:00') AND "region"."state_prov" NOT LIKE '%ON%' AND "payment"."member_no" >= 7541