SELECT AVG("provider"."region_no") as agg_0, SUM("provider"."state_prov") as agg_1 FROM "charge" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no" LEFT OUTER JOIN "catery" ON "charge"."catery_no" = "catery"."catery_no"  WHERE "catery"."catery_desc" NOT LIKE '%Tra%vel%' AND "charge"."statement_no" <= 22981 AND ("charge"."charge_amt" BETWEEN 1212.4978736474372 AND 3898.9932483414123 OR "charge"."charge_amt" >= 268.81237113257515)