SELECT SUM("provider"."country") as agg_0, COUNT(*) as agg_1, SUM("provider"."phone_no" + "region"."mail_code") as agg_2 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no"  WHERE "member"."corp_no" >= 181.81990087838153