SELECT MIN("provider"."mail_code" + "provider"."provider_no") as agg_0, COUNT(*) as agg_1 FROM "catery" LEFT OUTER JOIN "charge" ON "catery"."catery_no" = "charge"."catery_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no"  WHERE "catery"."catery_desc" LIKE '%Groceries%' AND "charge"."charge_amt" <= 3800.60340651527 AND "charge"."charge_no" BETWEEN 1461788 AND 1795372 AND "provider"."expr_dt" != '2000-10-12 10:41:23' AND "provider"."region_no" = 6