SELECT COUNT(*) as agg_0, MAX("member"."member_no") as agg_1, AVG("member"."country") as agg_2 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE ("statement"."member_no" >= 33932 OR "statement"."due_dt" NOT LIKE '%1999%-09-02%') AND ("statement"."statement_dt" IN ('1999-09-13 00:00:00') OR ("statement"."statement_dt" IN ('1999-08-13 00:00:00') AND "statement"."statement_dt" LIKE '%00:00:00%'))