SELECT MAX("corporation"."corp_code") as agg_0 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no"  WHERE ("region"."country" != 'Ke' OR ("region"."region_no" != 20 AND "region"."country" LIKE '%Br%')) AND ("member"."street" = ' ' OR "member"."lastname" LIKE '%DORR%')