SELECT SUM("corporation"."country" + "corporation"."corp_code") as agg_0, AVG("corporation"."country" + "corporation"."region_no") as agg_1 FROM "corporation"  WHERE "corporation"."corp_no" <= 493 AND "corporation"."state_prov" BETWEEN nan AND nan AND "corporation"."corp_name" NOT LIKE '%Ma%kersLtd.%' AND ("corporation"."region_no" != 43 OR ("corporation"."country" BETWEEN nan AND nan AND "corporation"."region_no" = 5))