SELECT SUM("charge"."charge_no") as agg_0, SUM("charge"."catery_no") as agg_1 FROM "member" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE "member"."firstname" IN ('BTOMWBKF', 'DFDZYFGCXTSWJO') AND "payment"."member_no" >= 7357 AND "member"."curr_balance" BETWEEN 0.0 AND 1.0 AND "member"."expr_dt" NOT LIKE '%2000-1%0-12%' AND "member"."lastname" LIKE '%XAVIER%'