SELECT MIN("provider"."region_no" + "corporation"."corp_no") as agg_0 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no"  WHERE "corporation"."expr_dt" LIKE '%2004-10-1%2%' OR ("corporation"."expr_dt" LIKE '%10:41:26%' AND "corporation"."expr_dt" != '2004-10-12 10:41:26')