SELECT AVG("statement"."statement_no") as agg_0, COUNT(*) as agg_1, AVG("member"."prev_balance") as agg_2 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no"  WHERE "statement"."due_dt" NOT LIKE '%00:00:00%' AND "region"."street" IN ('444 Fourth St.', '111 First St.', '222 Second St.', '333 Third St.', '777 Seventh St', '555 Fifth St St', '888 Eighth St.')