SELECT MAX("charge"."statement_no" + "charge"."member_no") as agg_0, COUNT(*) as agg_1, SUM("catery"."catery_no") as agg_2 FROM "charge" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "catery" ON "charge"."catery_no" = "catery"."catery_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no"  WHERE "statement"."statement_dt" IN ('1999-08-13 00:00:00') AND "region"."mail_code" BETWEEN nan AND nan AND ("region"."city" IN ('Budapest', 'Mombassa', 'Sao Paulo', 'Toronto', 'Brussels', 'Tokyo') OR "region"."city" NOT LIKE '%Goteborg%' OR "region"."city" LIKE '%Tor%onto%')