SELECT MIN("region"."region_code") as agg_0 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no"  WHERE ("corporation"."mail_code" BETWEEN nan AND nan OR "corporation"."mail_code" IS NULL) AND "member"."expr_dt" != '2000-10-12 10:43:19' AND ("region"."country" LIKE '%Br%' OR "region"."country" != 'Ja')