SELECT MIN("member"."photograph" + "member"."member_code") as agg_0, SUM("region"."mail_code") as agg_1 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no"  WHERE "region"."region_name" LIKE '%China%' AND "region"."country" IN ('Sw', 'Ke', 'Br') AND "provider"."issue_dt" IN ('1999-10-13 10:41:20', '1999-10-13 10:41:22', '1999-10-13 10:41:19', '1999-10-13 10:41:23', '1999-10-13 10:41:21') AND "provider"."mail_code" IS NOT NULL