SELECT AVG("statement"."statement_code") as agg_0, SUM("statement"."member_no" + "statement"."statement_amt") as agg_1, MIN("statement"."statement_no") as agg_2 FROM "statement"  WHERE "statement"."statement_dt" IN ('1999-08-13 00:00:00') AND ("statement"."statement_dt" IN ('1999-08-13 00:00:00') OR ("statement"."statement_amt" <= 13756.74626134933 AND "statement"."statement_dt" IN ('1999-08-13 00:00:00'))) AND "statement"."due_dt" = '1999-10-03 00:00:00' AND "statement"."statement_no" BETWEEN 4590 AND 10593