SELECT MAX("member"."curr_balance") as agg_0, SUM("charge"."catery_no" + "region"."region_code") as agg_1 FROM "member" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE "region"."country" LIKE '%Br%' AND ("member"."issue_dt" NOT LIKE '%1%999-10-13%' OR "member"."firstname" IN ('CFVRUTJSSLON', 'MVE', 'DGQLGJRVBLDPLA', 'QKSMQSTOJPWAV', 'EPYIPKUAIMFCDT', 'ERJWQNFHYOMDDR', 'BTOMWBKF', 'DFDZYFGCXTSWJO') OR "member"."issue_dt" != '1999-10-13 10:43:09') AND "region"."region_name" IN ('Eastern Europea', 'North American', 'Japan') AND "charge"."provider_no" BETWEEN 743 AND 2419