SELECT SUM("catery"."catery_no") as agg_0, MIN("charge"."charge_amt") as agg_1, AVG("provider"."mail_code" + "provider"."region_no") as agg_2 FROM "catery" LEFT OUTER JOIN "charge" ON "catery"."catery_no" = "charge"."catery_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no"  WHERE "catery"."catery_desc" LIKE '%Lodgin%g%' AND "catery"."catery_no" = 5