SELECT MAX("member"."member_no" + "member"."region_no") as agg_0 FROM "charge" LEFT OUTER JOIN "catery" ON "charge"."catery_no" = "catery"."catery_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no"  WHERE "catery"."catery_desc" IN ('Clothing', 'Home Supplies', 'Travel', 'Lodging', 'Meals') AND "charge"."member_no" >= 33957 AND "catery"."catery_code" BETWEEN nan AND nan AND "member"."issue_dt" IN ('1999-10-13 10:42:35', '1999-10-13 10:41:40', '1999-10-13 10:43:36')