SELECT MIN("statement"."statement_no") as agg_0 FROM "member" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no"  WHERE "member"."curr_balance" <= 0.0 AND "statement"."statement_dt" = '1999-09-13 00:00:00' AND "payment"."payment_dt" LIKE '%199%9-10-03%' AND "member"."lastname" != 'YEONG'