SELECT MAX("corporation"."mail_code" + "provider"."region_no") as agg_0, COUNT(*) as agg_1, SUM("corporation"."region_no") as agg_2 FROM "provider" LEFT OUTER JOIN "region" ON "provider"."region_no" = "region"."region_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no"  WHERE ("corporation"."region_no" = 1 OR "corporation"."corp_name" = 'Corp. Expert Sail MakersInc.') AND ("region"."mail_code" BETWEEN nan AND nan OR "region"."state_prov" NOT LIKE '%ON%')