SELECT MIN("corporation"."state_prov" + "corporation"."corp_no") as agg_0, MAX("member"."member_no") as agg_1 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no"  WHERE "corporation"."corp_name" = 'Corp. Apex LightingLtd.' AND "member"."expr_dt" = '2000-10-12 10:42:23'