SELECT MAX("charge"."member_no") as agg_0, COUNT(*) as agg_1, SUM("provider"."mail_code") as agg_2 FROM "catery" LEFT OUTER JOIN "charge" ON "catery"."catery_no" = "charge"."catery_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no"  WHERE "charge"."catery_no" <= 49 AND "provider"."provider_no" <= 1686 AND "catery"."catery_desc" NOT LIKE '%Lodgin%g%'