SELECT SUM("statement"."statement_amt") as agg_0, MIN("member"."phone_no" + "member"."member_no") as agg_1 FROM "member" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no"  WHERE "region"."country" NOT LIKE '%C%a%' AND "statement"."statement_dt" LIKE '%1999-09-%13%' AND "statement"."statement_code" BETWEEN nan AND nan