SELECT MIN("region"."mail_code" + "provider"."provider_no") as agg_0 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "charge" ON "provider"."provider_no" = "charge"."provider_no" LEFT OUTER JOIN "catery" ON "charge"."catery_no" = "catery"."catery_no"  WHERE "region"."city" IN ('Goteborg', 'Mombassa', 'Sao Paulo') AND "charge"."member_no" >= 322 AND "region"."city" NOT LIKE '%Tokyo%'