SELECT COUNT(*) as agg_0, AVG("statement"."statement_code" + "statement"."statement_no") as agg_1 FROM "member" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no"  WHERE "statement"."statement_code" BETWEEN nan AND nan AND "statement"."due_dt" NOT LIKE '%00:00:00%' AND "member"."issue_dt" = '1999-10-13 10:43:13' AND ("statement"."statement_dt" LIKE '%00:00:0%0%' OR "statement"."member_no" <= 21931) AND "member"."curr_balance" >= 0.0