SELECT MAX("charge"."charge_no" + "statement"."statement_code") as agg_0, AVG("member"."corp_no") as agg_1, SUM("charge"."statement_no") as agg_2 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no" LEFT OUTER JOIN "charge" ON "member"."member_no" = "charge"."member_no" LEFT OUTER JOIN "catery" ON "charge"."catery_no" = "catery"."catery_no"  WHERE "member"."expr_dt" != '2000-10-12 10:42:31' AND "catery"."catery_desc" IN ('Communication')