SELECT MIN("region"."mail_code" + "member"."photograph") as agg_0, SUM("charge"."catery_no") as agg_1 FROM "provider" LEFT OUTER JOIN "charge" ON "provider"."provider_no" = "charge"."provider_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no"  WHERE "statement"."statement_dt" IN ('1999-08-13 00:00:00') AND "provider"."issue_dt" = '1999-10-13 10:41:21'