SELECT AVG("charge"."catery_no") as agg_0, MAX("provider"."phone_no") as agg_1, MIN("provider"."provider_no") as agg_2 FROM "catery" LEFT OUTER JOIN "charge" ON "catery"."catery_no" = "charge"."catery_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no"  WHERE "catery"."catery_desc" IN ('Misc', 'Meals', 'Groceries', 'Travel', 'Clothing', 'Lodging', 'Home Supplies', 'Electronics', 'Communication') AND "charge"."member_no" BETWEEN 35684 AND 49769