SELECT MAX("statement"."statement_code") as agg_0, MAX("member"."middleinitial") as agg_1, COUNT(*) as agg_2 FROM "region" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no"  WHERE "member"."city" != ' ' AND "statement"."statement_dt" LIKE '%0%0:00:00%' AND "region"."state_prov" NOT LIKE '%ON%' AND "region"."country" IN ('Ca', 'Br')