SELECT MIN("member"."mail_code") as agg_0 FROM "provider" LEFT OUTER JOIN "region" ON "provider"."region_no" = "region"."region_no" LEFT OUTER JOIN "charge" ON "provider"."provider_no" = "charge"."provider_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no"  WHERE "member"."phone_no" BETWEEN nan AND nan AND "region"."region_name" = 'Western Europea' AND "payment"."payment_dt" LIKE '%00:00:00%'