SELECT SUM("provider"."country" + "charge"."member_no") as agg_0, COUNT(*) as agg_1, MIN("provider"."provider_code" + "provider"."region_no") as agg_2 FROM "charge" LEFT OUTER JOIN "catery" ON "charge"."catery_no" = "catery"."catery_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no"  WHERE "provider"."provider_code" BETWEEN nan AND nan AND "charge"."catery_no" BETWEEN 29 AND 48