SELECT MAX("charge"."member_no" + "provider"."state_prov") as agg_0 FROM "catery" LEFT OUTER JOIN "charge" ON "catery"."catery_no" = "charge"."catery_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no"  WHERE ("charge"."provider_no" >= 1248 OR "charge"."member_no" <= 8763) AND ("catery"."catery_desc" NOT LIKE '%M%isc%' OR "catery"."catery_code" BETWEEN nan AND nan) AND ("provider"."provider_name" NOT LIKE '%Sa%' OR "provider"."provider_code" BETWEEN nan AND nan)