SELECT AVG("payment"."payment_no" + "member"."photograph") as agg_0, COUNT(*) as agg_1 FROM "payment" LEFT OUTER JOIN "member" ON "payment"."member_no" = "member"."member_no" LEFT OUTER JOIN "region" ON "member"."region_no" = "region"."region_no"  WHERE "region"."street" NOT LIKE '%555%' AND "payment"."payment_dt" IN ('1999-10-03 00:00:00') AND "member"."expr_dt" LIKE '%2000-%10-12%' AND "region"."region_code" IS NOT NULL