SELECT MAX("member"."state_prov") as agg_0 FROM "provider" LEFT OUTER JOIN "charge" ON "provider"."provider_no" = "charge"."provider_no" LEFT OUTER JOIN "region" ON "provider"."region_no" = "region"."region_no" LEFT OUTER JOIN "corporation" ON "region"."region_no" = "corporation"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no"  WHERE "corporation"."corp_name" = 'Corp. Driven MachineryInc.'