SELECT COUNT(*) as agg_0, COUNT(*) as agg_1 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "charge" ON "provider"."provider_no" = "charge"."provider_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no"  WHERE "member"."lastname" LIKE '%UNAKLET%' AND "member"."country" IS NOT NULL AND "member"."expr_dt" NOT LIKE '%2000-%10-12%' AND "charge"."statement_no" <= 5916