SELECT MAX("provider"."provider_code" + "charge"."statement_no") as agg_0 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "charge" ON "provider"."provider_no" = "charge"."provider_no" LEFT OUTER JOIN "catery" ON "charge"."catery_no" = "catery"."catery_no"  WHERE "corporation"."corp_name" NOT LIKE '%Stones%Ltd.%' AND "region"."country" NOT LIKE '%Br%'