SELECT MAX("provider"."state_prov") as agg_0 FROM "catery" LEFT OUTER JOIN "charge" ON "catery"."catery_no" = "charge"."catery_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no"  WHERE "charge"."charge_no" BETWEEN 262404 AND 1722767 AND "member"."lastname" IN ('JONES', 'BARR', 'LING', 'KAHRMANN', 'NELSON', 'INFANTE', 'MAKAI')