SELECT MAX("member"."photograph" + "charge"."charge_amt") as agg_0, COUNT(*) as agg_1, MIN("member"."phone_no") as agg_2 FROM "region" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no" LEFT OUTER JOIN "charge" ON "provider"."provider_no" = "charge"."provider_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no"  WHERE "region"."state_prov" IS NULL