SELECT MAX("member"."mail_code" + "statement"."member_no") as agg_0, MAX("member"."photograph") as agg_1, COUNT(*) as agg_2 FROM "statement" LEFT OUTER JOIN "member" ON "statement"."member_no" = "member"."member_no"  WHERE "statement"."member_no" >= 12158 AND "statement"."due_dt" NOT LIKE '%1999-09-02%' AND "member"."lastname" NOT LIKE '%HAHN%' AND "statement"."statement_no" >= 16254