SELECT AVG("payment"."payment_code" + "payment"."payment_amt") as agg_0, MAX("member"."member_code" + "member"."member_no") as agg_1 FROM "charge" LEFT OUTER JOIN "catery" ON "charge"."catery_no" = "catery"."catery_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no" LEFT OUTER JOIN "payment" ON "member"."member_no" = "payment"."member_no"  WHERE "catery"."catery_desc" = 'Travel' AND ("catery"."catery_desc" IN ('Meals', 'Communication', 'Home Supplies', 'Groceries', 'Entertainment', 'Clothing') OR ("catery"."catery_desc" NOT LIKE '%Ente%rtainment%' AND "catery"."catery_desc" LIKE '%En%tertainment%'))