SELECT MIN("charge"."charge_code" + "provider"."country") as agg_0, MIN("charge"."member_no") as agg_1, MAX("provider"."region_no" + "member"."state_prov") as agg_2 FROM "catery" LEFT OUTER JOIN "charge" ON "catery"."catery_no" = "charge"."catery_no" LEFT OUTER JOIN "member" ON "charge"."member_no" = "member"."member_no" LEFT OUTER JOIN "statement" ON "member"."member_no" = "statement"."member_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no"  WHERE "member"."corp_no" <= 287.963871943322