SELECT MAX("charge"."charge_no") as agg_0, SUM("member"."member_no") as agg_1, AVG("member"."middleinitial") as agg_2 FROM "charge" LEFT OUTER JOIN "catery" ON "charge"."catery_no" = "catery"."catery_no" LEFT OUTER JOIN "provider" ON "charge"."provider_no" = "provider"."provider_no" LEFT OUTER JOIN "region" ON "provider"."region_no" = "region"."region_no" LEFT OUTER JOIN "member" ON "region"."region_no" = "member"."region_no"  WHERE "region"."region_name" IN ('Mid East / Sout', 'Eastern Europea', 'Scandanavian', 'Western Europea', 'North American', 'South American') AND ("charge"."statement_no" BETWEEN 4457 AND 6878 OR "charge"."catery_no" >= 21 OR "charge"."statement_no" BETWEEN 8076 AND 29893) AND "catery"."catery_desc" LIKE '%Communication%'