SELECT MIN("corporation"."state_prov") as agg_0, AVG("region"."region_code" + "provider"."provider_no") as agg_1, SUM("provider"."provider_code") as agg_2 FROM "corporation" LEFT OUTER JOIN "region" ON "corporation"."region_no" = "region"."region_no" LEFT OUTER JOIN "provider" ON "region"."region_no" = "provider"."region_no"  WHERE "region"."street" LIKE '%S%t%' AND "region"."country" != 'Ca' AND "region"."state_prov" LIKE '%ON%'