SELECT MAX("charge"."charge_amt") as agg_0, MAX("provider"."provider_no") as agg_1, MIN("provider"."phone_no") as agg_2 FROM "provider" LEFT OUTER JOIN "charge" ON "provider"."provider_no" = "charge"."provider_no" LEFT OUTER JOIN "region" ON "provider"."region_no" = "region"."region_no"  WHERE "provider"."state_prov" BETWEEN nan AND nan AND "provider"."expr_dt" NOT LIKE '%10:41:21%' AND "region"."state_prov" LIKE '%ON%' AND "region"."region_no" BETWEEN 16 AND 27